import{_ as d,c as r,o as a,ag as e}from"./chunks/framework.OaOo95RB.js";const u=JSON.parse('{"title":"macos快捷操作","description":"","frontmatter":{"title":"macos快捷操作","created":"2025-09-07 16:57","updated":"2025-09-07T00:00:00.000Z","origin":"week-36","type":"knowledge","status":"draft","tags":["tools"],"links":[]},"headers":[],"relativePath":"10-knowledge/250903-0735-macos快捷操作.md","filePath":"10-knowledge/250903-0735-macos快捷操作.md"}'),o={name:"10-knowledge/250903-0735-macos快捷操作.md"};function h(l,t,i,s,n,b){return a(),r("div",null,[...t[0]||(t[0]=[e('<h2 id="tl-dr-≤3点" tabindex="-1">TL;DR（≤3点） <a class="header-anchor" href="#tl-dr-≤3点" aria-label="Permalink to &quot;TL;DR（≤3点）&quot;">​</a></h2><ul><li></li><li></li><li></li></ul><h2 id="what-是什么" tabindex="-1">What（是什么） <a class="header-anchor" href="#what-是什么" aria-label="Permalink to &quot;What（是什么）&quot;">​</a></h2><ul><li></li></ul><h2 id="why-为什么这么做-何时使用" tabindex="-1">Why（为什么这么做/何时使用） <a class="header-anchor" href="#why-为什么这么做-何时使用" aria-label="Permalink to &quot;Why（为什么这么做/何时使用）&quot;">​</a></h2><ul><li></li></ul><h2 id="how-最小复现配方-≤5步" tabindex="-1">How（最小复现配方，≤5步） <a class="header-anchor" href="#how-最小复现配方-≤5步" aria-label="Permalink to &quot;How（最小复现配方，≤5步）&quot;">​</a></h2><ul><li></li></ul><h2 id="gotchas-坑点与边界" tabindex="-1">Gotchas（坑点与边界） <a class="header-anchor" href="#gotchas-坑点与边界" aria-label="Permalink to &quot;Gotchas（坑点与边界）&quot;">​</a></h2><ul><li></li></ul><h2 id="raw-notes" tabindex="-1">Raw Notes <a class="header-anchor" href="#raw-notes" aria-label="Permalink to &quot;Raw Notes&quot;">​</a></h2><p>太棒了！给你一份面向“纯键盘流”的 macOS（MacBook Air M4）快捷键速查表。尽量覆盖：全局切换、窗口/桌面管理、文本导航、截图、终端、VS Code/Cursor、主流浏览器。建议先在「系统设置 → 键盘」里开启“键盘导航（Full Keyboard Access）”。</p><h1 id="必备系统设置" tabindex="-1">必备系统设置 <a class="header-anchor" href="#必备系统设置" aria-label="Permalink to &quot;必备系统设置&quot;">​</a></h1><ul><li>开启全键盘访问：系统设置 → 键盘 → 键盘导航 → 开启（用 Tab/箭头键选菜单与对话框控件）</li><li>调整修饰键：系统设置 → 键盘 → 键盘快捷键 → 修饰键（建议把 Caps Lock 映射为 ⌃Control）</li><li>触控板角落呼出操作（可选）：系统设置 → 桌面与程序坞 → 热角（配合键盘更顺手）</li></ul><hr><h1 id="全局与应用切换" tabindex="-1">全局与应用切换 <a class="header-anchor" href="#全局与应用切换" aria-label="Permalink to &quot;全局与应用切换&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>应用切换</td><td>⌘Tab（按住 ⌘ 再点 Tab 循环），反向：⌘⇧Tab</td></tr><tr><td>在当前应用的多个窗口间切换</td><td>⌘<code>（数字 1 左边的键），反向：⌘⇧</code></td></tr><tr><td>隐藏当前应用</td><td>⌘H</td></tr><tr><td>隐藏其他应用</td><td>⌥⌘H</td></tr><tr><td>退出应用</td><td>⌘Q</td></tr><tr><td>打开 Spotlight</td><td>⌘Space（或 ⌃Space，依设置）</td></tr><tr><td>Siri/表情与符号</td><td>地球/🌐fn + Space（或 ⌃⌘Space 调出符号面板）</td></tr></tbody></table><hr><h1 id="窗口与多桌面-spaces" tabindex="-1">窗口与多桌面（Spaces） <a class="header-anchor" href="#窗口与多桌面-spaces" aria-label="Permalink to &quot;窗口与多桌面（Spaces）&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>关闭窗口/标签</td><td>⌘W（多数应用对标签同样有效）</td></tr><tr><td>最小化窗口</td><td>⌘M</td></tr><tr><td>全屏/退出全屏</td><td>⌃⌘F</td></tr><tr><td>Mission Control（总览）</td><td>⌃↑（或 F3）</td></tr><tr><td>应用窗口总览（App Exposé）</td><td>⌃↓</td></tr><tr><td>显示桌面</td><td>F11（或使用热角）</td></tr><tr><td>切换桌面（Space）</td><td>⌃→ / ⌃←</td></tr><tr><td>将窗口拖到其他桌面</td><td>Mission Control 中把窗口拖上方的桌面</td></tr></tbody></table><blockquote><p><strong>磁贴/分屏</strong>：原生无统一快捷键；可用第三方（见文末）映射“左半屏/右半屏/最大化”等。</p></blockquote><hr><h1 id="标签页与文档导航-多数应用通用" tabindex="-1">标签页与文档导航（多数应用通用） <a class="header-anchor" href="#标签页与文档导航-多数应用通用" aria-label="Permalink to &quot;标签页与文档导航（多数应用通用）&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>新建标签</td><td>⌘T</td></tr><tr><td>关闭标签</td><td>⌘W</td></tr><tr><td>还原关闭的标签</td><td>⌘⇧T</td></tr><tr><td>下/上一个标签</td><td>⌃Tab / ⌃⇧Tab（或 ⌘⇧] / ⌘⇧[）</td></tr><tr><td>跳转到第 n 个标签</td><td>⌘1…⌘9</td></tr><tr><td>查找/替换</td><td>⌘F / ⌘⌥F（或 ⌘⌥G 查找下一个）</td></tr></tbody></table><hr><h1 id="文本编辑与光标移动-系统级-emacs-风格也适用于终端" tabindex="-1">文本编辑与光标移动（系统级 &amp; Emacs 风格也适用于终端） <a class="header-anchor" href="#文本编辑与光标移动-系统级-emacs-风格也适用于终端" aria-label="Permalink to &quot;文本编辑与光标移动（系统级 &amp; Emacs 风格也适用于终端）&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>行首/行末</td><td>⌘← / ⌘→（很多编辑器也支持 ⌃A / ⌃E）</td></tr><tr><td>上/下/左/右移字符</td><td>方向键（也可 ⌃P/⌃N/⌃B/⌃F）</td></tr><tr><td>按词移动</td><td>⌥← / ⌥→</td></tr><tr><td>顶部/底部</td><td>⌘↑ / ⌘↓</td></tr><tr><td>删除光标前一词</td><td>⌥⌫（Option+Delete）</td></tr><tr><td>向前删除（Delete 键行为）</td><td>fn+⌫</td></tr><tr><td>删除到行尾</td><td>⌃K（终端/多数文本框有效）</td></tr><tr><td>撤销/重做</td><td>⌘Z / ⌘⇧Z</td></tr><tr><td>粘贴并匹配样式</td><td>⌥⇧⌘V（部分应用支持）</td></tr></tbody></table><hr><h1 id="截图与录屏" tabindex="-1">截图与录屏 <a class="header-anchor" href="#截图与录屏" aria-label="Permalink to &quot;截图与录屏&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>全屏截图</td><td>⇧⌘3</td></tr><tr><td>选区截图</td><td>⇧⌘4（按 Space 变为窗口截图）</td></tr><tr><td>截图/录屏工具栏</td><td>⇧⌘5（可定时器、录屏）</td></tr></tbody></table><hr><h1 id="终端-terminal-iterm2-zsh-bash" tabindex="-1">终端（Terminal/iTerm2/zsh/bash） <a class="header-anchor" href="#终端-terminal-iterm2-zsh-bash" aria-label="Permalink to &quot;终端（Terminal/iTerm2/zsh/bash）&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>中断当前进程</td><td>⌃C</td></tr><tr><td>发送 EOF / 退出输入</td><td>⌃D</td></tr><tr><td>将作业挂起到后台</td><td>⌃Z（前台恢复：<code>fg</code>）</td></tr><tr><td>清屏</td><td>⌃L</td></tr><tr><td>历史搜索</td><td>⌃R（反向增量搜索）</td></tr><tr><td>光标到行首/行末</td><td>⌃A / ⌃E</td></tr><tr><td>删除到行首/行尾</td><td>⌃U / ⌃K</td></tr><tr><td>删除前一个单词</td><td>⌃W（或 ⌥⌫）</td></tr><tr><td>按词移动</td><td>⌥B / ⌥F</td></tr><tr><td>打开新标签/分屏（iTerm2 可自定义）</td><td>⌘T / ⌘D（竖分）/ ⌘⇧D（横分）</td></tr></tbody></table><blockquote><p>iTerm2 推荐在 <strong>Preferences → Profiles → Keys</strong> 自定义分屏/切窗/切标签等为 ⌘⌥←/→、⌘⌥1…9。</p></blockquote><hr><h1 id="vs-code-cursor-大体一致" tabindex="-1">VS Code / Cursor（大体一致） <a class="header-anchor" href="#vs-code-cursor-大体一致" aria-label="Permalink to &quot;VS Code / Cursor（大体一致）&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>命令面板</td><td>⌘⇧P</td></tr><tr><td>快速打开文件</td><td>⌘P</td></tr><tr><td>全局搜索</td><td>⌘⇧F</td></tr><tr><td>侧边栏开关</td><td>⌘B</td></tr><tr><td>集成终端</td><td>⌃`</td></tr><tr><td>分屏编辑</td><td>⌘\\（反斜杠）</td></tr><tr><td>在编辑器组间切换</td><td>⌘1 / ⌘2 / ⌘3…</td></tr><tr><td>在标签间切换</td><td>⌃Tab / ⌃⇧Tab</td></tr><tr><td>关闭标签/全部</td><td>⌘W / ⌘K ⌘W</td></tr><tr><td>重命名符号</td><td>F2</td></tr><tr><td>跳转到定义/悬浮查看</td><td>F12 / ⌥F12（Peek）</td></tr><tr><td>多光标选词</td><td>⌘D（重复选中下一个匹配）</td></tr><tr><td>添加上下光标</td><td>⌥⌘↑ / ⌥⌘↓</td></tr><tr><td>移动行 / 复制行</td><td>⌥↑/↓ ；⇧⌥↑/↓</td></tr><tr><td>注释</td><td>⌘/（行）；⇧⌥A（块）</td></tr><tr><td>Zen 模式</td><td>⌘K Z</td></tr><tr><td>键位查看/自定义</td><td>⌘K ⌘S</td></tr></tbody></table><blockquote><p>Cursor 的 AI 面板/聊天键位可在 <strong>Settings → Keyboard Shortcuts</strong> 自行设定（与 VS Code 同入口）。</p></blockquote><hr><h1 id="浏览器-chrome-edge-safari-常用" tabindex="-1">浏览器（Chrome/Edge/Safari 常用） <a class="header-anchor" href="#浏览器-chrome-edge-safari-常用" aria-label="Permalink to &quot;浏览器（Chrome/Edge/Safari 常用）&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>新建/关闭/恢复标签</td><td>⌘T / ⌘W / ⌘⇧T</td></tr><tr><td>切换标签</td><td>⌃Tab / ⌃⇧Tab；或 ⌘1…⌘9 跳转指定</td></tr><tr><td>地址栏焦点</td><td>⌘L</td></tr><tr><td>刷新/强刷</td><td>⌘R / ⌘⇧R（Safari：Reader 模式是 ⇧⌘R）</td></tr><tr><td>前进/后退</td><td>⌘] / ⌘[</td></tr><tr><td>新建窗口/隐私窗口</td><td>⌘N / ⌘⇧N</td></tr><tr><td>页面内查找</td><td>⌘F</td></tr></tbody></table><hr><h1 id="finder-文件操作" tabindex="-1">Finder / 文件操作 <a class="header-anchor" href="#finder-文件操作" aria-label="Permalink to &quot;Finder / 文件操作&quot;">​</a></h1><table tabindex="0"><thead><tr><th>功能</th><th>快捷键</th></tr></thead><tbody><tr><td>新建窗口/标签</td><td>⌘N / ⌘T</td></tr><tr><td>跳转路径栏（输入路径）</td><td>⌘⇧G</td></tr><tr><td>预览（Quick Look）</td><td>空格</td></tr><tr><td>新建文件夹</td><td>⇧⌘N</td></tr><tr><td>显示/隐藏隐藏文件</td><td>⌘⇧.（点）</td></tr><tr><td>复制/剪切/粘贴</td><td>⌘C / ⌥⌘V（剪切粘贴）/ ⌘V</td></tr></tbody></table><hr><h1 id="提升窗口管理效率-可选第三方" tabindex="-1">提升窗口管理效率（可选第三方） <a class="header-anchor" href="#提升窗口管理效率-可选第三方" aria-label="Permalink to &quot;提升窗口管理效率（可选第三方）&quot;">​</a></h1><ul><li><strong>Rectangle</strong>（免费）：给窗口平铺/半屏/三分之二等映射成快捷键。常用：⌥⌘←/→（左/右半屏）、⌥⌘↑/↓（上/下半屏）、⌥⌘Enter（最大化）。</li><li><strong>Hammerspoon + yabai/skhd（进阶）</strong>：完全键盘化平铺与脚本化管理。</li><li><strong>Raycast/Alfred</strong>：全局启动器 + 跨应用动作（键盘为王）。</li></ul><hr><h1 id="一次性配置清单-建议" tabindex="-1">一次性配置清单（建议） <a class="header-anchor" href="#一次性配置清单-建议" aria-label="Permalink to &quot;一次性配置清单（建议）&quot;">​</a></h1><ol><li>键盘导航：开启；Caps → Control</li><li>Spotlight：保留 ⌘Space；给 Raycast（如有）设置 ⌥Space</li><li>Rectangle：启用半屏/三分屏/最大化的快捷键</li><li>VS Code/Cursor：把“在编辑器组间切换”“切换标签”“多光标”等设置成你最顺手的组合</li><li>iTerm2：把“分屏/切屏/切标签”映射为 ⌘⌥方向键 + ⌘数字</li></ol><hr><p>如果你愿意，我可以按你的<strong>具体偏好键位</strong>（比如把分屏、贴左、贴右、最大化都统一到 ⌥⌘+方向键）生成一张 A4「个人化快捷键海报」（PDF/PNG）。</p>',52)])])}const m=d(o,[["render",h]]);export{u as __pageData,m as default};
