import{_ as d,c as e,o as a,ag as r}from"./chunks/framework.CQuhCYrb.js";const C=JSON.parse('{"title":"OCR调研","description":"","frontmatter":{"title":"OCR调研","created":"2025-09-07 17:03","updated":"2025-09-07T00:00:00.000Z","origin":"week-34","type":"report","status":"draft","tags":[],"links":[]},"headers":[],"relativePath":"50-reports/250820-0754-OCR调研.md","filePath":"50-reports/250820-0754-OCR调研.md"}'),o={name:"50-reports/250820-0754-OCR调研.md"};function n(s,t,i,l,h,c){return a(),e("div",null,[...t[0]||(t[0]=[r('<h2 id="raw-notes" tabindex="-1">Raw Notes <a class="header-anchor" href="#raw-notes" aria-label="Permalink to &quot;Raw Notes&quot;">​</a></h2><h1 id="ocr-技术前沿调研-2025-年-8-月" tabindex="-1">OCR 技术前沿调研（2025 年 8 月） <a class="header-anchor" href="#ocr-技术前沿调研-2025-年-8-月" aria-label="Permalink to &quot;OCR 技术前沿调研（2025 年 8 月）&quot;">​</a></h1><h2 id="背景与现状" tabindex="-1">背景与现状 <a class="header-anchor" href="#背景与现状" aria-label="Permalink to &quot;背景与现状&quot;">​</a></h2><p>光学字符识别（OCR）技术在 2025 年已趋成熟，但并非完全“解决”的问题。它在打印文本上的准确率可达 99% 以上，但在手写、模糊图像、多语言或复杂布局（如表格、公式、水印）场景下，仍需人类干预，且整体准确率低于人类水平。 主要进步包括集成大型语言模型（LLM）和视觉-语言模型（VLM），如 Transformer 架构，提升了对复杂文档的语义理解、实时处理和多模态支持（如 LaTeX 公式识别、签名检测）。新兴趋势：开源模型主导创新，云服务提供高可扩展性；LLM 驱动的 OCR 可达 99.56% 准确率，尤其在标准文档上。 浏览器端实时 OCR（如 WebGPU 支持）也已成为现实。</p><h2 id="关键进步与趋势" tabindex="-1">关键进步与趋势 <a class="header-anchor" href="#关键进步与趋势" aria-label="Permalink to &quot;关键进步与趋势&quot;">​</a></h2><ul><li>AI 集成：传统 OCR（如 Tesseract）结合深度学习和 LLM，提升手写识别和上下文纠错。示例：使用 NLP 推断缺失字符，或 CV 检测图像质量问题。</li><li>开源主导：2025 年，许多顶级模型开源，支持 90+ 语言、高分辨率图像（如 4K）和边缘设备部署。基准测试（如 OCRBench）显示 VLM 如 Qwen2-VL 在多语言和文档理解上领先。</li><li>商业应用：云 API 如 Mistral OCR 和 AWS Textract 专注于大规模处理、RAG（Retrieval-Augmented Generation）集成和隐私保护。</li><li>挑战：手写/草书识别准确率较低；需结合其他 ML 技术（如结构化数据提取）才能实现端到端自动化。</li><li>新兴模型：如 Nanonets-OCR-s（基于 Qwen2.5-VL-3B），开源（Apache 2.0），在复杂表格、公式和语义标记上优于 Mistral OCR API，尤其在 LaTeX 和水印处理。</li></ul><h2 id="_2025年ocr模型比较" tabindex="-1">2025年OCR模型比较 <a class="header-anchor" href="#_2025年ocr模型比较" aria-label="Permalink to &quot;2025年OCR模型比较&quot;">​</a></h2><table tabindex="0"><thead><tr><th>模型名称</th><th>类型</th><th>关键特征</th><th>用例</th><th>基准/比较</th></tr></thead><tbody><tr><td>Nanonets-OCR-s</td><td>开源 (Apache 2.0)</td><td>基于 Qwen2.5-VL-3B；LaTeX 公式、签名/水印检测、复杂表格（Markdown/HTML 输出）、图像描述；语义结构理解。</td><td>学术文档、合同提取、RAG 系统。</td><td>优于 Mistral OCR 在表格和公式上；OCRBench 高分。</td></tr><tr><td>Mistral OCR</td><td>商业 API</td><td>多语言支持、Markdown 输出；处理 PDF、表格、手写、公式；LLM 驱动，高准确率（94.89% 整体）。</td><td>文档理解、大规模扫描、RAG 集成。</td><td>优于 Google Document AI（83.42%）；成本更低。</td></tr><tr><td>GOT-OCR2</td><td>开源</td><td>580M 参数；端到端统一模型，处理纯文本、表格、图表、公式；格式化输出。</td><td>复杂布局文档、学术/工业应用。</td><td>在 FUNSD/CORD 数据集上高性能；优于传统 OCR。</td></tr><tr><td>Qwen2-VL</td><td>开源 (多种大小: 2B-72B)</td><td>90+ 语言、高分辨率视频帧/多页文档；DocVQA/MathVista 基准领先。</td><td>多模态分析、视频 OCR、长文档。</td><td>OCRBench 顶级；开源替代 Claude/GPT-4V。</td></tr><tr><td>PaddleOCR</td><td>开源</td><td>轻量级；多语言、布局分析；速度快于 Tesseract。</td><td>场景文本识别、移动部署。</td><td>在速度/准确率上领先；支持 PP-Structure。</td></tr><tr><td>docTR</td><td>开源</td><td>两阶段方法（检测+识别）；CPU/GPU 支持；高性能于收据/表单。</td><td>快速文档处理、发票提取。</td><td>FUNSD/CORD 数据集 SOTA；推荐用于工作场景。</td></tr><tr><td>Surya</td><td>开源 (Python 工具包)</td><td>行级文本检测、90+ 语言；布局分析。</td><td>结构化文档、批量处理。</td><td>速度/准确率优于 Tesseract。</td></tr><tr><td>Moondream2</td><td>开源 (&lt;2B 参数)</td><td>紧凑 VLM；实时扫描；适用于表格/表单。</td><td>移动/边缘设备、实时应用。</td><td>资源受限环境 SOTA。</td></tr><tr><td>Tesseract</td><td>开源 (经典)</td><td>LSTM 引擎；手写支持；免费。</td><td>基础 OCR、集成到其他工具。</td><td>广泛使用，但被新 VLM 超越；免费引擎首选。</td></tr><tr><td>Azure Computer Vision</td><td>商业云</td><td>多语言、手写识别；集成 LLM。</td><td>企业级、大数据处理。</td><td>高准确率；云扩展性强。</td></tr></tbody></table>',8)])])}const O=d(o,[["render",n]]);export{C as __pageData,O as default};
